class Content extends React.Component {

//render decides what is displayed on the page
render() {
    return (
        <main className="content">

            <img className="content__image" alt="Insectopedia Superstar Insect" src="https://img.icons8.com/officel/80/000000/bee.png"/>

            <ContentLabel />
            
            <p className="content__paragraph">Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.</p>
            
        </main>
    )
}




}

export default Content;
